using System;\nusing System.Text.RegularExpressions;\n\nclass Example\n{\n    public static void Main()\n    {\n        string myString = "cat,dog,\"0 = OFF, 1 = ON\",lion,tiger,'R = red, G = green, B = blue',bear";\n        \n        Console.WriteLine("\nmyString is ...\n\t" + myString + "\n");\n\n        // Regex pattern to match CSV values with optional quotes\n        Regex regex = new Regex("(?:^|,)(?:\"((?:[^\"]|\"\")*)\"|'((?:[^']|'')*)'|([^\",]+))");\n        MatchCollection matches = regex.Matches(myString);\n\n        int j = 0;\n        foreach (Match match in matches)\n        {\n            // Use Group2 for 'quoted values', Group3 for double-quoted values, and Group4 for unquoted values\n            string value = match.Groups[1].Value ?? match.Groups[2].Value ?? match.Groups[3].Value;\n            Console.WriteLine(j++ + " \t" + value);\n        }\n    }\n}