using System;\nusing System.Data;\nusing System.Data.OleDb;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "Provider=Microsoft.Jet.OLEDB.4.0;Data Source=C:\\path\\to\\your\\database.mdb;";\n        \n        using (OleDbConnection connection = new OleDbConnection(connectionString))\n        {\n            connection.Open();\n\n            // Get schema information about the database\n            DataTable schemaTable = connection.GetSchema("Tables");\n\n            DataSet dataSet = new DataSet();\n            \n            foreach (DataRow row in schemaTable.Rows)\n            {\n                string tableName = row["TABLE_NAME"].ToString();\n                \n                // Skip system tables for Access Jet Database Engine\n                if (!tableName.StartsWith("MSys") && !tableName.StartsWith("~"))\n                {   \n                    OleDbDataAdapter adapter = new OleDbDataAdapter($"SELECT * FROM [{tableName}]", connection);\n                    \n                    DataTable table = new DataTable(tableName);\n                    adapter.Fill(table);\n\n                    dataSet.Tables.Add(table);\n                }\n            }\n\n            // Now you have all the tables in DataSet\n            Console.WriteLine("Tables loaded into DataSet:");\n            foreach (DataTable dt in dataSet.Tables)\n            {\n                Console.WriteLine(dt.TableName);\n            }\n        }\n    }\n}