using System;\nusing System.Data.SqlClient; // Include necessary namespaces\n\npublic partial class Default : System.Web.UI.Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        if (!IsPostBack)\n        {\n            // Initialize dropdowns or load initial data here\n        }\n    }\n\n    protected void ddlCountry_SelectedIndexChanged(object sender, EventArgs e)\n    {\n        SaveSelectionToDatabase("Country", ((DropDownList)sender).SelectedValue);\n    }\n\n    protected void ddlCity_SelectedIndexChanged(object sender, EventArgs e)\n    {\n        SaveSelectionToDatabase("City", ((DropDownList)sender).SelectedValue);\n    }\n\n    private void SaveSelectionToDatabase(string type, string value)\n    {\n        // Example database save logic\n        using (SqlConnection conn = new SqlConnection("YourConnectionString"))\n        {\n            conn.Open();\n            using (SqlCommand cmd = new SqlCommand($"UPDATE YourTable SET {type}Id=@Value WHERE SomeCondition", conn))\n            {\n                cmd.Parameters.AddWithValue("@Value", value);\n                cmd.ExecuteNonQuery();\n            }\n        }\n    }\n}