static void DeleteFiles(string d, string ext)\n{\n    try\n    {\n        DirectoryInfo di = new DirectoryInfo(d);\n        \n        if (!di.Exists)\n        {\n            Console.WriteLine($"Directory {d} does not exist.");\n            return;\n        }\n\n        FileInfo[] fi = di.GetFiles(ext);\n\n        Console.WriteLine("Number of files found = " + fi.Length + ".");\n        foreach (FileInfo f in fi)\n        {\n            try\n            {\n                Console.WriteLine("Deleting file: " + f.FullName);\n                f.Delete();\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine($"Error deleting {f.FullName}: {ex.Message}");\n            }\n        }\n    }\n    catch (Exception ex)\n    {\n        Console.WriteLine($"Error accessing directory {d}: {ex.Message}");\n    }\n}