using System.IO;\nusing PdfSharp.Pdf;\nusing PdfSharp.Pdf.IO;\n\npublic void AddTextToExistingPdf(string inputFilePath, string outputFilePath, string textToAdd)\n{\n    // Open the document to import pages from it.\n    PdfDocument inputDocument = PdfReader.Open(inputFilePath, PdfDocumentOpenMode.Import);\n\n    // Create a new PDF document\n    PdfDocument outputDocument = new PdfDocument();\n\n    foreach (PdfPage page in inputDocument.Pages)\n    {\n        outputDocument.AddPage(page);\n    }\n\n    // Get the first page from the modified document.\n    PdfPage pageToEdit = outputDocument.Pages[0];\n    XGraphics gfx = XGraphics.FromPdfPage(pageToEdit);\n\n    XFont font = new XFont("Verdana", 20, XFontStyle.BoldItalic);\n    gfx.DrawString(textToAdd, font, XBrushes.DarkBlue, new XRect(0, 0, pageToEdit.Width, pageToEdit.Height), XStringFormats.Center);\n\n    // Save the document...\n    outputDocument.Save(outputFilePath);\n}