public ActionResult Update(MyModel model)\n{\n    if (TryUpdateModel(model))\n    {\n        // Your update logic here\n    }\n\n    if (!ModelState.IsValid)\n    {\n        foreach (var state in ModelState)\n        {\n            var propertyName = state.Key;\n            var errorMessages = state.Value.Errors.Select(err => err.ErrorMessage);\n            \n            // Log the property name and its associated error messages\n            foreach (var message in errorMessages)\n            {\n                Console.WriteLine($"Property: {propertyName} - Error: {message}");\n            }\n        }\n\n        // Return the view with the model to show validation errors\n        return View(model);\n    }\n\n    // Redirect or return another action result on success\n    return RedirectToAction("Index");\n}