// GET: /Stationery/ShowHtml/5?asHtml=true\n[HttpGet]\n[ActionName("Show")]\npublic ContentResult ShowHtml(int id, bool asHtml)\n{\n    if (!asHtml)\n        return RedirectToAction("ShowXml", new { id = id });\n\n    var result = Stationery.Load(id);\n    return Content(result.GetHtml());\n}\n\n// GET: /Stationery/ShowXml/5\n[HttpGet]\n[ActionName("Show")]\npublic XmlResult ShowXml(int id)\n{\n    var result = Stationery.Load(id);\n    return new XmlResult(result);\n}