using System;\nusing System.Net;\n\npublic class ContentTypeChecker\n{\n    public static string GetContentType(string url)\n    {\n        HttpWebRequest request = (HttpWebRequest)WebRequest.Create(url);\n        request.Method = "HEAD"; // Important to send only the headers\n\n        using (HttpWebResponse response = (HttpWebResponse)request.GetResponse())\n        {\n            return response.ContentType;\n        }\n    }\n\n    public static void Main()\n    {\n        string url1 = "http://example.com/rss feed.xml";\n        string url2 = "http://example.com/page.html";\n\n        Console.WriteLine($"Content-Type of {url1}: {GetContentType(url1)}");\n        Console.WriteLine($"Content-Type of {url2}: {GetContentType(url2)}");\n    }\n}