using System;\n\npublic static class SimpleEncryptor\n{\n    public static string Encrypt(string text, int shift = 3)\n    {\n        char[] buffer = text.ToCharArray();\n        for (int i = 0; i < buffer.Length; i++)\n        {\n            char letter = buffer[i];\n            if (char.IsLetter(letter))\n            {\n                char offset = char.IsUpper(letter) ? 'A' : 'a';\n                letter = (char)(((letter + shift - offset) % 26) + offset);\n                buffer[i] = letter;\n            }\n        }\n        return new string(buffer);\n    }\n\n    public static string Decrypt(string text, int shift = 3)\n    {\n        return Encrypt(text, 26 - shift); // reversing the shift\n    }\n}