using System;\nusing System.Globalization;\nusing System.Text.RegularExpressions;\n\npublic class SEOHelper\n{\n    public static string SEO_makeTitle(string objTitle)\n    {\n        if (string.IsNullOrWhiteSpace(objTitle))\n        {\n            return "";\n        }\n\n        // Convert to lowercase\n        string strTitle = objTitle.ToLowerInvariant();\n\n        // Normalize the string to decompose combined characters\n        strTitle = Regex.Replace(\n            strTitle.Normalize(NormalizationForm.FormD),\n            @"[\p{Mn}]", "");\n\n        // Remove any character that is not a letter or number, replace with spaces\n        strTitle = Regex.Replace(strTitle, "[^a-z0-9\\s-]+", " ");\n\n        // Replace multiple spaces and hyphens with a single space\n        strTitle = Regex.Replace(strTitle, "(\\s|[-])+"," ").Trim();\n\n        // Replace spaces with hyphens\n        return strTitle.Replace(" ", "-");\n    }\n}