using System;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        PerformanceCounter cpuCounter = new PerformanceCounter("Process", "% Processor Time", Process.GetCurrentProcess().ProcessName);\n        cpuCounter.NextValue(); // First call returns 0, discard it\n\n        while (true)\n        {\n            float cpuUsage = cpuCounter.NextValue();\n            if (cpuUsage > 25) // Adjust threshold as needed\n            {\n                Console.WriteLine($"CPU spike detected at {DateTime.Now}: {cpuUsage}%");\n            }\n            System.Threading.Thread.Sleep(1000); // Check every second\n        }\n    }\n}