using System;\nusing System.Net;\n\nclass FileChecker\n{\n    public static void CheckFileExistenceAndSize(string url)\n    {\n        try\n        {\n            var request = (HttpWebRequest)WebRequest.Create(url);\n            request.Method = "HEAD"; // Use HEAD method to get only headers\n\n            using (var response = (HttpWebResponse)request.GetResponse())\n            {\n                if (response.StatusCode == HttpStatusCode.OK)\n                {\n                    Console.WriteLine("File exists on the server.");\n                    \n                    // Get file size from Content-Length header\n                    if (long.TryParse(response.Headers[HttpResponseHeader.ContentLength], out long fileSize))\n                    {\n                        Console.WriteLine($"File Size: {fileSize} bytes");\n                    }\n                    else\n                    {\n                        Console.WriteLine("Content-Length header not available or not a valid number.");\n                    }\n                }\n                else\n                {\n                    Console.WriteLine($"Failed to retrieve file. Status Code: {response.StatusCode}");\n                }\n            }\n        }\n        catch (WebException ex)\n        {\n            if (ex.Response != null && ((HttpWebResponse)ex.Response).StatusCode == HttpStatusCode.NotFound)\n            {\n                Console.WriteLine("File does not exist on the server.");\n            }\n            else\n            {\n                Console.WriteLine("An error occurred while checking the file: " + ex.Message);\n            }\n        }\n    }\n\n    static void Main(string[] args)\n    {\n        string url = "http://example.com/path/to/file.txt";\n        CheckFileExistenceAndSize(url);\n    }\n}