using System;\nusing System.IO;\nusing System.Reflection;\nusing System.Xml;\n\npublic class EmbeddedResourceXmlResolver : XmlUrlResolver\n{\n    private Assembly _assembly;\n    public EmbeddedResourceXmlResolver(Assembly assembly)\n    {\n        _assembly = assembly ?? throw new ArgumentNullException(nameof(assembly));\n    }\n\n    public override object GetEntity(Uri absoluteUri, string role, Type ofObjectToReturn)\n    {\n        if (absoluteUri != null && "http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd".Equals(absoluteUri.AbsoluteUri))\n        {\n            var resourceName = $"{_assembly.GetName().Name}.xhtml1-strict.dtd"; // Adjust this to match your resource name path\n            using (var stream = _assembly.GetManifestResourceStream(resourceName))\n            {\n                if (stream != null)\n                    return new StreamReader(stream);\n            }\n        }\n\n        return base.GetEntity(absoluteUri, role, ofObjectToReturn);\n    }\n}\n\npublic class Program\n{\n    public static void Main()\n    {\n        var assembly = Assembly.GetExecutingAssembly();\n        var settings = new XmlReaderSettings\n        {\n            ProhibitDtd = false,\n            DtdProcessing = DtdProcessing.Parse,\n            XmlResolver = new EmbeddedResourceXmlResolver(assembly)\n        };\n\n        using (var reader = XmlReader.Create("your-xhtml-file.xhtml", settings))\n        {\n            var xmlDoc = new XmlDocument();\n            xmlDoc.Load(reader);\n\n            // Proceed with your XML processing\n        }\n    }\n}