protected void Session_Start(object sender, EventArgs e)\n{\n    // Assuming you have a method to determine if a user is an admin or standard user.\n    bool isAdmin = IsUserAdmin();\n\n    if (isAdmin)\n    {\n        // Set session timeout for admin users to 3 hours\n        HttpContext.Current.Session.Timeout = 180; // in minutes\n    }\n    else\n    {\n        // Set session timeout for standard users to 30 minutes\n        HttpContext.Current.Session.Timeout = 30; // in minutes\n    }\n}\n\nprivate bool IsUserAdmin()\n{\n    // Implement your logic to determine if the current user is an admin.\n    return Roles.IsUserInRole("Admin"); // Example using ASP.NET roles.\n}