using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        // Define the application directory under AppData\n        string appDataPath = Path.Combine(Environment.GetFolderPath(Environment.SpecialFolder.ApplicationData), "Foo");\n        \n        // Ensure the directory exists\n        if (!Directory.Exists(appDataPath))\n        {\n            Directory.CreateDirectory(appDataPath);\n        }\n        \n        // Define the configuration file path\n        string configFilePath = Path.Combine(appDataPath, "config.txt");\n\n        // Write a sample text to the configuration file\n        File.WriteAllText(configFilePath, "Last selected option: Option1");\n        \n        Console.WriteLine("Configuration written successfully.");\n    }\n}