using System;\nusing System.Collections.Generic;\n\npublic static class MyMath\n{\n    public static int MostFreq(int[] _M, out int x)\n    {\n        Dictionary<int, int> frequencyMap = new Dictionary<int, int>();\n        int maxFreq = 0;\n        \n        foreach (int num in _M)\n        {\n            if (!frequencyMap.ContainsKey(num))\n                frequencyMap[num] = 0;\n            \n            frequencyMap[num]++;\n            \n            if (frequencyMap[num] > maxFreq)\n            {\n                maxFreq = frequencyMap[num];\n                x = num;\n            }\n        }\n        \n        return maxFreq;\n    }\n}\n\nclass Program\n{\n    static void Main()\n    { \n        int[] M = {4, 5, 6, 4, 4, 3, 5, 3};\n        int freqNumber;\n        int frequency = MyMath.MostFreq(M, out freqNumber);\n        Console.WriteLine("The most frequent item = {0} with frequency = {1}", freqNumber, frequency);\n    }\n}