using System;\nusing System.ComponentModel.DataAnnotations.Schema;\nusing System.Data.Entity;\n\npublic class ChartConfiguration : EntityTypeConfiguration<Chart>\n{\n    public ChartConfiguration()\n    {\n        ToTable("Chart");\n        Property(c => c.Type).IsRequired();\n    }\n}\n\npublic class LineChartConfiguration : EntityTypeConfiguration<LineChart>\n{\n    public LineChartConfiguration()\n    {\n        ToTable("LineChart");\n        HasKey(t => t.ChartId);\n        Property(t => t.ChartId)\n            .HasDatabaseGeneratedOption(DatabaseGeneratedOption.None);\n        HasRequired(t => t.Chart)\n            .WithOptional(t => t.LineChartDetails)\n            .HasForeignKey(t => t.Id)\n            .WillCascadeOnDelete(false);\n    }\n}\n\n// Similar configurations for PieChart and BarChart