// In Global.asax.cs\n\nvoid Session_Start(object sender, EventArgs e)\n{\n    // Store the current time when the session starts\n    Session["SessionStartTime"] = DateTime.Now;\n}\n\nprotected void Application_BeginRequest(Object sender, EventArgs e)\n{\n    // Calculate the age of the session whenever needed\n    if (Session["SessionStartTime"] != null)\n    {\n        TimeSpan sessionAge = DateTime.Now - (DateTime)Session["SessionStartTime"];\n        // 'sessionAge' now holds the duration since the session started\n        // You can log this, use it in logic, etc.\n    }\n}