using System;\nusing System.IO;\nusing System.Net;\n\npublic void MakeWebRequest()\n{\n    HttpWebRequest request = (HttpWebRequest)WebRequest.Create("http://www.test1.com/");\n    request.Method = "GET";\n\n    try\n    {\n        using (var response = request.GetResponse() as HttpWebResponse)\n        {\n            if (response == null || !response.ContentType.StartsWith("text/html"))\n                return;\n\n            // Read the response body\n            using (Stream responseStream = response.GetResponseStream())\n            {\n                if (responseStream != null)\n                {\n                    var reader = new StreamReader(responseStream);\n                    string responseBody = reader.ReadToEnd();\n                    Console.WriteLine("Response Body: " + responseBody);\n                }\n            }\n        }\n    }\n    catch (WebException ex)\n    {\n        // Handle the error\n        using (var errorResponse = ex.Response as HttpWebResponse)\n        {\n            if (errorResponse != null && errorResponse.ContentType.StartsWith("text/html"))\n            {\n                using (Stream responseStream = errorResponse.GetResponseStream())\n                {\n                    if (responseStream != null)\n                    {\n                        var reader = new StreamReader(responseStream);\n                        string errorMessage = reader.ReadToEnd();\n                        Console.WriteLine("Error Message: " + errorMessage);\n                    }\n                }\n            }\n            else\n            {\n                throw ex; // Re-throw the exception if it's not an HTML error response\n            }\n        }\n    }\n}