void Session_Start(object sender, EventArgs e)\n{\n    // Check the current request scheme\n    var isSecure = HttpContext.Current.Request.IsSecureConnection;\n\n    // Create a new session cookie with a unique name based on the scheme\n    string cookieName = (isSecure ? "SecureSessionId" : "InsecureSessionId");\n\n    HttpContext.Current.Session.SessionIDManager.InitializeRequest(HttpContext.Current, true, out bool redirected, out bool cookieAdded);\n\n    if (!redirected && !cookieAdded)\n    {\n        // Replace the default session ID cookie with a custom one\n        HttpCookie cookie = new HttpCookie(cookieName, HttpContext.Current.Session.SessionID);\n        cookie.Secure = isSecure; // Ensure the cookie is marked as Secure for HTTPS\n        HttpContext.Current.Response.Cookies.Add(cookie);\n    }\n}