private Control FindControlRecursive(Control root, string id)\n{\n    if (root.ID == id)\n        return root;\n\n    foreach (Control c in root.Controls)\n    {\n        Control t = FindControlRecursive(c, id);\n        if (t != null) return t;\n    }\n    return null;\n}\n\n// Usage\nLabel label = (Label)FindControlRecursive(this.Page, "Label1");\n\nif (label != null)\n{\n    // You can now manipulate the label\n    label.Text = "This label text was set from ASCX.";\n}\nelse\n{\n    throw new Exception("Label not found.");\n}