using System;\nusing System.Web;\n\npublic class MyRestHandler : IHttpHandler {\n    public void ProcessRequest(HttpContext context) {\n        string pathInfo = context.Request.PathInfo;\n        \n        switch (context.Request.HttpMethod.ToUpper()) {\n            case "GET":\n                HandleGet(context);\n                break;\n            case "POST":\n                HandlePost(context);\n                break;\n            // Add PUT and DELETE cases as needed\n        }\n    }\n\n    private void HandleGet(HttpContext context) {\n        context.Response.ContentType = "application/json";\n        context.Response.Write("{\"message\":\"Hello World\"}");\n    }\n\n    private void HandlePost(HttpContext context) {\n        using (var reader = new System.IO.StreamReader(context.Request.InputStream)) {\n            string body = reader.ReadToEnd();\n            // Process the POST data...\n        }\n        context.Response.ContentType = "application/json";\n        context.Response.Write("{\"status\":\"success\"}");\n    }\n\n    public bool IsReusable { get { return false; } }\n}