using System;\nusing System.Collections.Generic;\nusing System.Windows.Forms;\n\npublic class MultiColumnListBoxExample : Form\n{\n    private ListBox listBox1;\n\n    public MultiColumnListBoxExample()\n    {\n        listBox1 = new ListBox\n        {\n            Location = new System.Drawing.Point(10, 10),\n            Width = 300,\n            DrawMode = DrawMode.OwnerDrawFixed\n        };\n        listBox1.DrawItem += ListBox1_DrawItem;\n        Controls.Add(listBox1);\n\n        // Sample data: a generic list of objects\n        var items = new List<CustomObject>\n        {\n            new CustomObject { Id = 1, Name = "Alice", Age = 30 },\n            new CustomObject { Id = 2, Name = "Bob", Age = 25 }\n        };\n\n        // Bind data to ListBox\n        foreach (var item in items)\n        {\n            var listBoxItem = new ListBoxItem($"{item.Name} - {item.Age}", item);\n            listBox1.Items.Add(listBoxItem);\n        }\n    }\n\n    private void ListBox1_DrawItem(object sender, DrawItemEventArgs e)\n    {\n        if (e.Index < 0) return;\n        e.DrawBackground();\n        var item = ((ListBoxItem)listBox1.Items[e.Index]);\n        e.Graphics.DrawString(item.DisplayText, e.Font, Brushes.Black, e.Bounds);\n        e.DrawFocusRectangle();\n    }\n\n    public static void Main()\n    {\n        Application.Run(new MultiColumnListBoxExample());\n    }\n}\n\npublic class CustomObject\n{\n    public int Id { get; set; }\n    public string Name { get; set; }\n    public int Age { get; set; }\n}\n\npublic class ListBoxItem\n{\n    public string DisplayText { get; set; }\n    public object Tag { get; set; }\n\n    public ListBoxItem(string displayText, object tag)\n    {\n        DisplayText = displayText;\n        Tag = tag;\n    }\n}