protected void Page_Init(object sender, EventArgs e)\n{\n    CreateDynamicControls();\n}\n\nprivate void CreateDynamicControls()\n{\n    // Create a Panel container.\n    Panel panel = new Panel();\n\n    // Create a TextBox dynamically.\n    TextBox textBox = new TextBox\n    {\n        ID = "dynamicTextBox"\n    };\n\n    // Add the TextBox to the Panel.\n    panel.Controls.Add(textBox);\n\n    // Create RequiredFieldValidator for the TextBox.\n    RequiredFieldValidator requiredFieldValidator = new RequiredFieldValidator()\n    {\n        ID = "dynamicTextBoxRequiredValidator",\n        ControlToValidate = textBox.ID,\n        ErrorMessage = "Text is required.",\n        Display = ValidatorDisplay.Static\n    };\n\n    // Optionally, add RegularExpressionValidator.\n    RegularExpressionValidator regexValidator = new RegularExpressionValidator()\n    {\n        ID = "dynamicTextBoxRegexValidator",\n        ControlToValidate = textBox.ID,\n        ValidationExpression = @"^[a-zA-Z]+$",\n        ErrorMessage = "Only letters are allowed.",\n        Display = ValidatorDisplay.Static\n    };\n\n    // Add validators to the Panel.\n    panel.Controls.Add(requiredFieldValidator);\n    panel.Controls.Add(regexValidator);\n\n    // Assume tableCell is a member variable or passed in.\n    TableCell tableCell = new TableCell();\n    tableCell.Controls.Add(panel);\n\n    TableRow row = new TableRow();\n    row.Cells.Add(tableCell);\n\n    Table dynamicTable = new Table { ID = "dynamicTable" };\n    dynamicTable.Rows.Add(row);\n\n    somePlaceHolder.Controls.Clear();\n    somePlaceHolder.Controls.Add(dynamicTable);\n}