using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string root = @"C:\x";\n        string relativeOrRootedPath = @"\y";\n\n        // Remove the leading directory separator if present\n        if (relativeOrRootedPath.StartsWith(Path.DirectorySeparatorChar.ToString()))\n        {\n            relativeOrRootedPath = relativeOrRootedPath.Substring(1);\n        }\n\n        string combinedPath = Path.Combine(root, relativeOrRootedPath);\n        Console.WriteLine(combinedPath); // Outputs: C:\x\y\n    }\n}