using System;\n\nnamespace HelloWorldApp\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            Console.WriteLine("Hello, World!");\n            // Example of using Console for input/output\n            \n            Console.Write("Please enter your name: ");\n            string userName = Console.ReadLine();\n            \n            Console.WriteLine($"Hello, {userName}!Welcome to C# programming.");\n            \n            // Example of using string interpolation and basic class usage\n            \n            Person person = new Person\n            {\n                FirstName = "John",\n                LastName = "Doe"\n            };\n            \n            Console.WriteLine($"This is an object property: {person.FullName}");\n        }\n    }\n\n    public class Person\n    {\n        public string FirstName { get; set; }\n        public string LastName { get; set; }\n\n        public string FullName => $"{FirstName} {LastName}";\n        // Property with expression-bodied member\n    }\n}