using System;\nusing System.IO;\nusing System.Text;\nusing HtmlAgilityPack;\n\nclass Program\n{\n    static void Main()\n    {\n        // Read the HTML file with Latin1 (ISO-8859-1) encoding\n        var htmlContent = File.ReadAllText("yourfile.html", Encoding.GetEncoding("ISO-8859-1"));\n\n        // Load the document into HtmlAgilityPack using a StringReader and specify the correct encoding\n        var doc = new HtmlDocument();\n        using (var stringReader = new StringReader(htmlContent))\n        {\n            doc.Load(stringReader, Encoding.GetEncoding("ISO-8859-1"));\n        }\n\n        // XPath query with Swedish characters\n        string xpathQuery = @"//h2[text()='FÃ¶retag']/../div//span[text()='Resultat:']/../div";\n\n        // Execute the XPath query\n        var nodes = doc.DocumentNode.SelectNodes(xpathQuery);\n\n        if (nodes != null)\n        {\n            foreach (var node in nodes)\n            {\n                Console.WriteLine(node.OuterHtml);\n            }\n        }\n        else\n        {\n            Console.WriteLine("No nodes found.");\n        }\n    }\n}