using System;\nusing ADOX;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "Provider=Microsoft.Jet.OLEDB.4.0;Data Source=C:\\path\\to\\your\\database.mdb;";\n        \n        using (Catalog catalog = new Catalog())\n        {\n            catalog.ActiveConnection = connectionString;\n            \n            Table table = (Table)catalog.Tables["YourTableName"];\n            \n            foreach (Constraint constraint in table.Constraints)\n            {\n                if (constraint.Type == KeyTypeEnum.adKeyPrimary)\n                {\n                    Console.WriteLine("Primary Key Constraint Name: " + constraint.Name);\n                }\n            }\n        }\n    }\n}