using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string sourceFilePath = @"C:\Path\To\Source\File.txt";\n        string destinationFilePath = @"C:\Path\To\Destination\File.txt";\n\n        while (true)\n        {\n            try\n            {\n                File.Copy(sourceFilePath, destinationFilePath, true);\n                Console.WriteLine("File copied successfully.");\n                break;\n            }\n            catch (IOException ex) when ((ex.HResult & 0xFFFF) == 32) // ERROR_SHARING_VIOLATION\n            {\n                Console.WriteLine("The file is currently in use. Retrying...");\n                System.Threading.Thread.Sleep(100); // Wait and retry\n            }\n        }\n    }\n}