public void CheckForFileRenamingDiscrepancies()\n{\n    string projPath = Server.MapPath("~");\n    var filesInSolution = Directory.GetFiles(projPath, "*", SearchOption.AllDirectories);\n    \n    foreach (var file in filesInSolution)\n    {\n        if (file.Contains("OLD_PAGE_NAME")) // Replace with the old page name\n        {\n            System.Diagnostics.Debug.WriteLine($"Old page reference found in: {file}");\n        }\n    }\n}