// Define the query using named parameters\nstring query = \n    "SELECT * FROM (" +\n    "   SELECT TOP [@pResultSet] StartDate, [ID] FROM (" +\n    "       SELECT TOP [@pNumToRetrieve] StartDate, [ID] FROM Story " +\n    "       ORDER BY StartDate DESC, [ID] DESC" +\n    "   ) AS foo " +\n    "   ORDER BY StartDate ASC, [ID] ASC" +\n    ") AS bar " +\n    "INNER JOIN Story AS t ON bar.ID = t.ID " +\n    "ORDER BY bar.StartDate DESC, bar.[ID] DESC";\n\n// Create and set the parameters with names\nvar pNumToRetrieve = new OleDbParameter("@pNumToRetrieve", OleDbType.Integer) { Value = numToGet };\nvar pResultSet = new OleDbParameter("@pResultSet", OleDbType.Integer) { Value = resultSet };\n\n// Add the parameters to the command\n_cmd.Parameters.Add(pNumToRetrieve);\n_cmd.Parameters.Add(pResultSet);\n\n// Set the command text\n_cmd.CommandText = query;