protected void Page_Load(object sender, EventArgs e)\n{\n    if (IsPostBack)\n        ValidateFile();\n}\n\nprivate void ValidateFile()\n{\n    string[] validExtensions = { ".xls", ".xlsx" };\n    var myPost = HttpContext.Current.Request.Files["fileUpload"];\n    string fileExtension = Path.GetExtension(myPost.FileName).ToLower();\n\n    bool isValidExtension = Array.Exists(validExtensions, ext => ext == fileExtension);\n\n    if (isValidExtension)\n    {\n        // Proceed with saving the file\n        string saveLocation = Server.MapPath("~/UploadedFiles/") + Path.GetFileName(myPost.FileName);\n        myPost.SaveAs(saveLocation); // Save to server\n    }\n    else\n    {\n        // Handle invalid file type\n        Response.Write("Invalid File type.");\n    }\n}