using System.Windows;\nusing System.Windows.Media;\n\npublic class MapControl : Canvas\n{\n    protected override void OnRender(DrawingContext drawingContext)\n    {\n        base.OnRender(drawingContext);\n\n        // Get the DPI scale factor\n        var dpiScale = VisualTreeHelper.GetDpi(this);\n        \n        // Convert ActualWidth from WPF units to physical inches\n        double actualWidthInches = this.ActualWidth / dpiScale.DPixelsPerInchX;\n        double actualHeightInches = this.ActualHeight / dpiScale.DPixelsPerInchY;\n\n        // Display or use the actual size in inches as needed\n    }\n}