using System;\nusing System.Windows.Forms;\n\npublic class WebAutoFiller : Form\n{\n    private WebBrowser webBrowser = new WebBrowser();\n\n    public WebAutoFiller()\n    {\n        webBrowser.Navigate("http://yourtargeturl.com");\n        webBrowser.DocumentCompleted += WebBrowser_DocumentCompleted;\n        this.Controls.Add(webBrowser);\n    }\n\n    private void WebBrowser_DocumentCompleted(object sender, WebBrowserDocumentCompletedEventArgs e)\n    {\n        // Wait for the page to fully load\n        Application.DoEvents();\n\n        // Get the element you want to interact with\n        HtmlElement inputField = webBrowser.Document.GetElementById("yourInputFieldId");\n        if (inputField != null)\n        {\n            inputField.Focus();\n            \n            // Simulate typing and pressing Enter\n            SendKeys.SendWait("yourSearchTerm{ENTER}");\n        }\n    }\n\n    [STAThread]\n    static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.SetCompatibleTextRenderingDefault(false);\n        Application.Run(new WebAutoFiller());\n    }\n}