using System;\nusing System.Data.SqlClient;\n\n// Example method to fetch and display records with correct timezone conversion\npublic void FetchAndDisplayRecords()\n{\n    using (SqlConnection conn = new SqlConnection("YourConnectionString"))\n    {\n        conn.Open();\n        using (SqlCommand command = new SqlCommand("SELECT Id, Data, CreatedAt FROM Records", conn))\n        {\n            SqlDataReader reader = command.ExecuteReader();\n\n            while (reader.Read())\n            {\n                int id = reader.GetInt32(0);\n                string data = reader.GetString(1);\n                DateTimeOffset createdAtUtc = reader.GetDateTimeOffset(2);\n\n                // Convert UTC to Local Time\n                DateTimeOffset localTime = createdAtUtc.ToLocalTime();\n\n                Console.WriteLine($"Record ID: {id}, Data: {data}, Created At (Local): {localTime}");\n            }\n        }\n    }\n}