using System;\nusing System.Runtime.InteropServices;\n\n[ComVisible(true)]\npublic interface IMyClass {\n    string GetSpecialCookieValue(string cookies);\n}\n\n[ComVisible(true), ClassInterface(ClassInterfaceType.None)]\npublic class MyClass : IMyClass {\n    public string GetSpecialCookieValue(string cookies) {\n        // Parse the cookies to find your special cookie\n        var cookieCollection = HttpUtility.ParseQueryString(cookies);\n        return cookieCollection["YourSpecialCookie"] ?? "Default";\n    }\n}