using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string filePath = "yourfile.jpg";\n\n        try\n        {\n            using (FileStream fs = new FileStream(filePath, FileMode.Open, FileAccess.Read))\n            {\n                if (fs.Length < 3)\n                {\n                    Console.WriteLine("File too short to be a JPEG.");\n                    return;\n                }\n\n                byte[] buffer = new byte[3];\n                int bytesRead = fs.Read(buffer, 0, 3);\n                \n                // Check for the first two bytes FF D8 which should be at the start of every JPEG file\n                if (bytesRead == 3 && buffer[0] == 0xFF && buffer[1] == 0xD8)\n                {\n                    Console.WriteLine("The file is likely a JPEG.");\n                }\n                else\n                {\n                    Console.WriteLine("The file is not a JPEG.");\n                }\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"An error occurred: {ex.Message}");\n        }\n    }\n}