[MetadataType(typeof(User.Metadata))]\npublic class User\n{\n    #region Metadata        \n    private class Metadata\n    {\n        [Required(ErrorMessage="Name is required")]\n        [StringLength(5, ErrorMessage="Max Length is 5")]\n        public string Name { get; set; }\n        \n        // Make BirthDay nullable and use DataType.Date for validation\n        [DataType(DataType.Date, ErrorMessage = "You have entered an invalid date.")]\n        public DateTime? BirthDay { get; set; }\n    }\n    #endregion\n\n    public int? ID { get; set; }\n    public string Name { get; set; }        \n    public DateTime? BirthDay { get; set; } // Changed to nullable DateTime\n}