// Pseudo-code for what a script might look like (not real C#)\nvoid MakeInternal(string assemblyPath)\n{\n    AssemblyDefinition assembly = AssemblyDefinition.ReadAssembly(assemblyPath);\n    foreach (TypeDefinition type in assembly.MainModule.Types)\n    {\n        if (!type.Namespace.StartsWith("YourApiNamespace"))\n        {\n            type.IsPublic = false;\n            type.IsNotPublic = true;\n\n            foreach (MethodDefinition method in type.Methods)\n            {\n                method.IsPublic = false;\n                method.IsFamily = false; // or set to private as necessary\n                method.IsPrivate = true; // depending on method accessibility desired\n            }\n        }\n    }\n\n    assembly.Write(assemblyPath);\n}