protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        PopulateTreeView();\n    }\n}\n\nprivate void PopulateTreeView()\n{\n    myTreeView.Nodes.Add(new TreeNode("Test 1", "Test One"));\n    myTreeView.Nodes.Add(new TreeNode("Test 2", "Test two"));\n    myTreeView.Nodes.Add(new TreeNode("Test 3", "Test three"));\n    myTreeView.Nodes.Add(new TreeNode("Test 4", "Test four"));\n    myTreeView.Nodes.Add(new TreeNode("Test 5", "Test five"));\n    myTreeView.Nodes.Add(new TreeNode("Test 6", "Test size"));\n}\n\nprotected void myTreeView_SelectedNodeChanged(object sender, EventArgs e)\n{\n    UpdateLabel();\n}\n\nprotected void myButton_Click(object sender, EventArgs e)\n{\n    int newIndex = -1;\n\n    if (myTreeView.SelectedNode != null && myTreeView.SelectedIndex < myTreeView.Nodes.Count - 1)\n    {\n        // Find the index of the currently selected node\n        newIndex = myTreeView.SelectedIndex + 1;\n    }\n    \n    if (newIndex > -1 && newIndex < myTreeView.Nodes.Count)\n    {\n        myTreeView.SelectedNode.Selected = false; // Deselect current node\n        myTreeView.Nodes[newIndex].Selected = true;   // Select new node\n        UpdateLabel(); // Update the label with the newly selected node text\n    }\n}\n\nprivate void UpdateLabel()\n{\n    if (myTreeView.SelectedNode != null)\n    {\n        myLabel.Text = myTreeView.SelectedNode.Value;\n    }\n}