using System.Windows;\nusing System.Windows.Media.Animation;\n\nnamespace WpfApp\n{\n    public partial class MainWindow : Window\n    {\n        public MainWindow()\n        {\n            InitializeComponent();\n        }\n\n        private void Button_Click(object sender, RoutedEventArgs e)\n        {\n            // Define a story board for animations\n            Storyboard storyboard = new Storyboard();\n\n            // Create a double animation with the desired behavior\n            DoubleAnimation heightAnimation = new DoubleAnimation(\n                fromValue: 100, \n                toValue: 200,\n                duration: new Duration(TimeSpan.FromSeconds(2)));\n\n            // Set up the target of the animation. Here we're animating the Height property.\n            Storyboard.SetTarget(heightAnimation, AnimatedRow);\n            Storyboard.SetTargetProperty(heightAnimation, new PropertyPath("Height.Value"));\n\n            storyboard.Children.Add(heightAnimation);\n\n            // Start the animation\n            storyboard.Begin();\n        }\n    }\n}