protected void Save(..)\n{   \n    // Do save stuff\n    DisplayAlertAndRedirect("The changes were saved Successfully", "Default.aspx");\n}\n\nprotected virtual void DisplayAlertAndRedirect(string message, string url)\n{\n    ClientScript.RegisterStartupScript(\n        this.GetType(),\n        Guid.NewGuid().ToString(),\n        $"alert('{message.Replace("'", @"\'").Replace("\n", "\\n").Replace("\r", "\\r")}'); window.location.href = '{url}';",\n        true\n    );\n}