using System;\nusing Microsoft.Office.Interop.Word;\n\npublic class HtmlToWordConverter\n{\n    public static void InsertHtmlIntoWord(string htmlContent, string outputPath)\n    {\n        // Create a new Word application\n        Application wordApp = new Application();\n        Document doc = wordApp.Documents.Add();\n\n        // Convert HTML content to Word format\n        Range range = doc.Range(0, 0);\n        range.PasteHTML(htmlContent);\n\n        // Save the document\n        doc.SaveAs2(outputPath, WdSaveFormat.wdFormatXMLDocument);\n        doc.Close();\n        wordApp.Quit();\n    }\n}\n\n// Example usage:\nHtmlToWordConverter.InsertHtmlIntoWord("<b>Bold Text</b> <i>Italic Text</i>", @"C:\output\formatteddoc.docx");