using System;\nusing System.Xml;\n\nclass Program\n{\n    static void Main()\n    {\n        XmlDocument doc = new XmlDocument();\n        doc.LoadXml("<a><b>zyx</b><b>wvu</b><b>tsr</b><b>qpo</b></a>");\n\n        // Select the node with the value 'tsr'.\n        XmlNode targetNode = doc.SelectSingleNode("a/b[.='tsr']");\n\n        if (targetNode != null)\n        {\n            // Count preceding siblings to determine position.\n            int position = targetNode.SelectNodes("preceding-sibling::b").Count + 1;\n\n            Console.WriteLine("Found at position = {0}", position);\n        }\n        else\n        {\n            Console.WriteLine("Node not found.");\n        }\n    }\n}