using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\npublic bool IsList(object value)\n{\n    if (value == null) return false;\n\n    Type type = value.GetType();\n    // Check if the object is an instance of IList<>\n    return typeof(IList<>).IsAssignableFrom(type.GetInterfaces()\n        .FirstOrDefault(t => t.IsGenericType && t.GetGenericTypeDefinition() == typeof(IList<>)));\n}\n\n// Usage example:\nvar myValue = new List<int>();\nConsole.WriteLine(IsList(myValue)); // True