public class PageLayoutDTO\n{\n    public int ID { get; set; }\n    // Add other properties needed by your JavaScript client here.\n}\n\n[WebMethod]\n[ScriptMethod(UseHttpGet = false, XmlSerializeString = true)]\npublic PageLayoutDTO GetPageLayout(string lid, int pid)\n{\n    if (!SystemModel.UserManager.HasLogin())\n        return null;\n\n    var layout = SystemModel.Layouts.GetPageLayout(pid);\n    if (layout == null) return null;\n\n    // Convert your LINQ-to-SQL object to a DTO\n    PageLayoutDTO dto = new PageLayoutDTO\n    {\n        ID = layout.ID,\n        // Map other properties here.\n    };\n    \n    dto.Tag = lid;\n    return dto;\n}