protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack) // Ensure this runs only once per page load\n    {\n        DataTable dt = GetDataFromDatabase(); // Assume this method fetches your data\n        if (dt.Rows.Count > 0 && dt.Rows[0]["IsActive"] != DBNull.Value)\n        {\n            chkIsActive.Checked = Convert.ToBoolean(dt.Rows[0]["IsActive"]);\n        }\n    }\n}