using System;\nusing System.Data.Entity;\nusing System.ComponentModel.DataAnnotations.Schema;\n\npublic class MyCustomContext : DbContext\n{\n    public DbSet<MyExistingModel> ExistingModels { get; set; }\n\n    protected override void OnModelCreating(DbModelBuilder modelBuilder)\n    {\n        // Example of mapping custom entities to tables and overriding column mappings.\n        modelBuilder.Entity<MyExistingModel>()\n            .ToTable("MyDatabaseTableName");\n\n        modelBuilder.Entity<MyExistingModel>()\n            .Property(e => e.ModelId)\n            .HasColumnName("DatabaseColumnName");\n        \n        base.OnModelCreating(modelBuilder);\n    }\n}\n\npublic class MyExistingModel\n{\n    public int ModelId { get; set; } // Example property\n    public string Name { get; set; }  // Example property\n}