protected void Page_Load(object sender, EventArgs e)\n{\n    // Condition to determine the order (this could be from a database or user preference setting)\n    bool reverseOrder = true; // This is just for demonstration\n\n    if (!IsPostBack)\n    {\n        // Create TextBox controls and add them to the PlaceHolder in default order\n        var textBox1 = new TextBox { ID = "TextBox1" };\n        var textBox2 = new TextBox { ID = "TextBox2" };\n        var textBox3 = new TextBox { ID = "TextBox3" };\n\n        PlaceHolder1.Controls.Add(textBox1);\n        PlaceHolder1.Controls.Add(textBox2);\n        PlaceHolder1.Controls.Add(textBox3);\n\n        // Reorder controls based on the condition\n        if (reverseOrder)\n        {\n            PlaceHolder1.Controls.SetRemoveAt(0, textBox1); // Remove textBox1 and store it\n            PlaceHolder1.Controls.SetRemoveAt(0, textBox2); // Move textBox2 to first position\n            PlaceHolder1.Controls.Add(textBox1); // Add textBox1 back to the end\n            PlaceHolder1.Controls.SetRemoveAt(0, textBox3); // Move textBox3 to first position\n        }\n    }\n}