ResponseSheet Tests = new ResponseSheet();\nDataSet ReturnData = new DataSet(); // Corrected name typo\nOracleDataAdapter da;\ntry\n{\n    using (OracleConnection conn = new OracleConnection(ConnString))\n    {\n        conn.Open();\n\n        OracleCommand cmd = new OracleCommand\n        {\n            Connection = conn,\n            CommandText = @"SELECT gsh.locid, locations.description, GSH.workorder, GSH.comstatus, GSH.teststatus, GSH.fireresult, GSH.lightresult, GSH.watercold, GSH.waterhot, GSH.responsedate, GSH.comments, GSH.testername \n                             FROM gsh_vhs_locations locs\n                             LEFT OUTER JOIN locations ON locs.maximoloc = locations.location\n                             LEFT OUTER JOIN gsh_vhs_comms GSH ON locs.LOCID = GSH.locid \n                             WHERE gsh.insertdate > SYSDATE - 7\n                             ORDER BY locid, locations.description, GSH.workorder, GSH.comstatus, GSH.teststatus, GSH.fireresult, GSH.lightresult, GSH.watercold, GSH.waterhot, GSH.responsedate, GSH.comments, GSH.testername"\n        };\n\n        da = new OracleDataAdapter(cmd); // Simplified creation of OracleDataAdapter\n        da.MissingMappingAction = MissingMappingAction.Error;\n        da.TableMappings.Add("Table", "ResponseSheet");\n        \n        int rowsAffected = da.Fill(ReturnData, "ResponseSheet"); // Store number of rows filled\n        Console.WriteLine($"{rowsAffected} rows retrieved."); // Log the number of rows for debugging\n\n        if (ReturnData.Tables.Count == 0 || ReturnData.Tables["ResponseSheet"].Rows.Count == 0)\n        {\n            Console.WriteLine("No data was returned from the query.");\n        }\n    }\n}\ncatch (Exception ex)\n{\n    Console.WriteLine(TimeStamp() + "Exception in Get Capture Report: " + ex.Message + " (TraceCode: 00019)");\n}\n\nreturn ReturnData;